Issue confirmed to be fixed in Eucalyptus 4.1.1 using [QA Shared Setup #2|https://eucalyptus.atlassian.net/wiki/display/QA/QA+Shared+Setups#QASharedSetups-SharedSetup#2(VPC)]

Eucalyptus Version:  4.1.1-0.0.23157.64.20150403git72f651b.el6

*Steps*

# Bundle, upload and registered [Ubuntu Vivid (15.04) Cloud image|http://cloud-images.ubuntu.com/vivid/current/]
# Created keypair
# Used the following VPC template:
{code}
{

    "AWSTemplateFormatVersion" : "2010-09-09",

    "Description" : "Cloudformation Example => Run instance with userdata in security group",

    "Parameters": {
        "UbuntuImageId": {
            "Description":"Ubuntu Image id",
            "Type":"String",
            "NoEcho":"False"
        },

        "UserKeyPair": {
            "Description":"User Key Pair",
            "Type":"String",
            "NoEcho":"False"
        }
    },

    "Resources" : {
        "DockerVPC": {
            "Type": "AWS::EC2::VPC",
            "Properties" : {
                "CidrBlock" : "10.0.0.0/16",
                "EnableDnsSupport" : "True",
                "EnableDnsHostnames" : "True",
                "InstanceTenancy" : "default",
                "Tags" : [ {"Key" : "Application", "Value" : { "Ref" : "AWS::StackId"}} ]
            }
        },

        "DockerSubnet": {
            "Type" : "AWS::EC2::Subnet",
            "Properties" : {
                "VpcId" : { "Ref" : "DockerVPC" },
                "CidrBlock" : "10.0.0.0/24",
                "Tags" : [ {"Key" : "Application", "Value" : { "Ref" : "AWS::StackId"} } ]
            }
        },

        "DockerInternetGateway" : {
            "Type" : "AWS::EC2::InternetGateway",
            "Properties" : {
                "Tags" : [ {"Key" : "Application", "Value" : { "Ref" : "AWS::StackId"} } ]
            }
        },

        "DockerAttachGateway" : {
            "Type" : "AWS::EC2::VPCGatewayAttachment",
            "Properties" : {
                "VpcId" : { "Ref" : "DockerVPC" },
                "InternetGatewayId" : { "Ref" : "DockerInternetGateway" }
            }
        },

        "DockerRouteTable": {
            "Type" : "AWS::EC2::RouteTable",
            "Properties" : {
                "VpcId" : { "Ref" : "DockerVPC" },
                "Tags" : [ {"Key" : "Application", "Value" : { "Ref" : "AWS::StackId"} } ]
            }
        },

        "DockerRoute" : {
            "Type" : "AWS::EC2::Route",
            "DependsOn" : "DockerAttachGateway",
            "Properties" : {
                "RouteTableId" : { "Ref" : "DockerRouteTable" },
                "DestinationCidrBlock" : "0.0.0.0/0",
                "GatewayId" : { "Ref" : "DockerInternetGateway" }
            }
        },

        "DockerSubnetRouteTableAssocaiation" : {
            "Type" : "AWS::EC2::SubnetRouteTableAssociation",
            "Properties" : {
                "SubnetId" : { "Ref" : "DockerSubnet" },
                "RouteTableId" : { "Ref" : "DockerRouteTable" }
            }
        },

        "DockerNetworkAcl" : {
            "Type" : "AWS::EC2::NetworkAcl",
            "Properties" : {
                "VpcId" : {"Ref" : "DockerVPC"},
                "Tags" : [ {"Key" : "Application", "Value" : { "Ref" : "AWS::StackId"} } ]
            }
        },

        "InboundSSHNetworkAclEntry" : {
            "Type" : "AWS::EC2::NetworkAclEntry",
            "Properties" : {
                "NetworkAclId" : {"Ref" : "DockerNetworkAcl"},
                "RuleNumber" : "101",
                "Protocol" : "6",
                "RuleAction" : "allow",
                "Egress" : "false",
                "CidrBlock" : "0.0.0.0/0",
                "PortRange" : {"From" : "22", "To" : "22"}
            }
        },

        "InboundResponsePortsNetworkAclEntry" : {
            "Type" : "AWS::EC2::NetworkAclEntry",
            "Properties" : {
                "NetworkAclId" : {"Ref" : "DockerNetworkAcl"},
                "RuleNumber" : "102",
                "Protocol" : "6",
                "RuleAction" : "allow",
                "Egress" : "false",
                "CidrBlock" : "0.0.0.0/0",
                "PortRange" : {"From" : "1024", "To" : "65535"}
            }
        },

        "OutBoundHTTPNetworkAclEntry" : {
            "Type" : "AWS::EC2::NetworkAclEntry",
            "Properties" : {
                "NetworkAclId" : {"Ref" : "DockerNetworkAcl"},
                "RuleNumber" : "100",
                "Protocol" : "6",
                "RuleAction" : "allow",
                "Egress" : "true",
                "CidrBlock" : "0.0.0.0/0",
                "PortRange" : {"From" : "80", "To" : "80"}
            }
        },

        "OutBoundHTTPSNetworkAclEntry" : {
            "Type" : "AWS::EC2::NetworkAclEntry",
            "Properties" : {
                "NetworkAclId" : {"Ref" : "DockerNetworkAcl"},
                "RuleNumber" : "101",
                "Protocol" : "6",
                "RuleAction" : "allow",
                "Egress" : "true",
                "CidrBlock" : "0.0.0.0/0",
                "PortRange" : {"From" : "443", "To" : "443"}
            }
        },

        "OutboundResponsePortsNetworkAclEntry" : {
            "Type" : "AWS::EC2::NetworkAclEntry",
            "Properties" : {
                "NetworkAclId" : {"Ref" : "DockerNetworkAcl"},
                "RuleNumber" : "102",
                "Protocol" : "6",
                "RuleAction" : "allow",
                "Egress" : "true",
                "CidrBlock" : "0.0.0.0/0",
                "PortRange" : {"From" : "1024", "To" : "65535"}
            }
        },

        "SubnetNetworkAclAssociation" : {
            "Type" : "AWS::EC2::SubnetNetworkAclAssociation",
            "Properties" : {
                "SubnetId" : { "Ref" : "DockerSubnet" },
                "NetworkAclId" : { "Ref" : "DockerNetworkAcl" }
            }
        },

        "DockerIPAddress" : {
            "Type" : "AWS::EC2::EIP",
            "DependsOn" : "DockerAttachGateway",
            "Properties" : {
                "Domain" : "vpc",
                "InstanceId" : { "Ref" : "DockerInstance" }
            }
        },

        "DockerSecurityGroup": {
            "Type": "AWS::EC2::SecurityGroup",
                "Properties": {
                    "GroupDescription" : "Security Group with Ingress Rule for DockerInstance",
                    "VpcId" : {"Ref" : "DockerVPC"},
                    "SecurityGroupIngress" : [
                        {
                            "IpProtocol" : "tcp",
                            "FromPort" : "22",
                            "ToPort" : "22",
                            "CidrIp" : "0.0.0.0/0"
                        }
                    ]
                }
        },

        "DockerInstance": {
            "Type": "AWS::EC2::Instance",
                "Properties": {
                    "ImageId" : { "Ref":"UbuntuImageId" },
                    "InstanceType" : "m1.xlarge",
                    "UserData" : { "Fn::Base64" : "#include https://get.docker.io" },
                    "Tags" : [ {"Key" : "Application", "Value" : { "Ref" : "AWS::StackId"} } ],
                    "NetworkInterfaces" : [{
                        "GroupSet" : [{ "Ref" : "DockerSecurityGroup" }],
                        "AssociatePublicIpAddress" : "true",
                        "DeviceIndex"  : "0",
                        "SubnetId"  : { "Ref" : "DockerSubnet" }
                    }],
                    "KeyName" : { "Ref" : "UserKeyPair" }
            }
        }
    },

    "Outputs" : {
        "InstanceId" : {
            "Description" : "InstanceId of the newly created EC2 instance",
            "Value" : { "Ref" : "DockerInstance" }
        },
        "AZ" : {
            "Description" : "Availability Zone of the newly created EC2 instance",
            "Value" : { "Fn::GetAtt" : [ "DockerInstance", "AvailabilityZone" ] }
        },
        "PublicIP" : {
            "Description" : "Public IP address of the newly created EC2 instance",
            "Value" : { "Fn::GetAtt" : [ "DockerInstance", "PublicIp" ] }
        }
    }
}
{code}
