# Using eucalyptus/admin credentials on region 'asap-rocky-2015', created 'devops' account, and downloaded creds
{code}
[root@h-08 ~]# euare-accountcreate -a devops
devops	001476132417
[root@h-08 ~]# euca-get-credentials -a devops -u admin devops-admin.zip
[root@h-08 ~]# mkdir devops
[root@h-08 ~]# mv devops-admin.zip devops/.
[root@h-08 ~]# cd devops/
[root@h-08 devops]# unzip devops-admin.zip
