        {
            "Effect": "Allow",
            "Action": "elasticloadbalancing:*",
            "Resource": "arn:aws:elasticloadbalancing:::loadbalancer/*"
        }
    ]
}

[root@h-08 ~]# eulb-describe-lbs --region devops-user01@asap-rocky-2015
