{code}
# Created two accounts under 'rick-ross' region:
{code}
# euare-accountcreate -w -a development-operations > .euca/devops-admins.ini --region euca-admin@rick-ross
# euare-accountcreate -w -a engineering > .euca/eng-admin.ini --region euca-admin@rick-ross
{code}
# Set up AWS profiles for both development-operations/admin and engineering/admin users
{code}
[root@e-37 ~]# cat .aws/credentials
[devops-admin]
aws_access_key_id = AKIABPCXDC7NOJMJLV5G
aws_secret_access_key = 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
[eng-admin]
aws_access_key_id = AKIABPOV2XJ52L3ISWEY
aws_secret_access_key = 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
{code}
# Created bucket under development-operations account; added FULL_CONTROL for engineering account:
{code}
[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3 mb s3://nats-io-logs --profile devops-admin
make_bucket: s3://nats-io-logs/

[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3api put-bucket-acl --grant-full-control id=001192988049 --bucket nats-io-logs --profile devops-admin

[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3api get-bucket-acl --bucket nats-io-logs --profile devops-admin
{
    "Owner": {
        "DisplayName": "development-operations",
        "ID": "5d1a3513a9232c1442491ba291142572f4326c4334b102d1c58544d3e49b3b22"
    },
    "Grants": [
        {
            "Grantee": {
                "DisplayName": "engineering",
                "ID": "d4c3e5949341f374454514d4f554141145422444943404f5f513913124761460"
            },
            "Permission": "FULL_CONTROL"
        },
        {
            "Grantee": {
                "DisplayName": "development-operations",
                "ID": "5d1a3513a9232c1442491ba291142572f4326c4334b102d1c58544d3e49b3b22"
            },
            "Permission": "FULL_CONTROL"
        }
    ]
}
{code}
# Tested both {{aws s3 rm}} and {{aws s3api delete-objects}} for multiple object delete.  For both tests, I used the engineering/admin user to upload 5 objects, then ran multiple object deletes.  To upload objects:
{code}
[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3 cp federated-region.json s3://nats-io-logs/federated-region.json --profile eng-admin
upload: ./federated-region.json to s3://nats-io-logs/federated-region.json
[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3 cp federated-region.json s3://nats-io-logs/federated-region-v2.json --profile eng-admin
upload: ./federated-region.json to s3://nats-io-logs/federated-region-v2.json
[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3 cp federated-region.json s3://nats-io-logs/federated-region-v3.json --profile eng-admin
upload: ./federated-region.json to s3://nats-io-logs/federated-region-v3.json
[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3 cp federated-region.json s3://nats-io-logs/federated-region-v4.json --profile eng-admin
upload: ./federated-region.json to s3://nats-io-logs/federated-region-v4.json
[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3 cp federated-region.json s3://nats-io-logs/federated-region-v5.json --profile eng-admin
upload: ./federated-region.json to s3://nats-io-logs/federated-region-v5.json
{code}
#* Multiple object delete using {{aws s3 rm}}
{code}
[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3 rm s3://nats-io-logs --recursive --profile eng-admin
delete: s3://nats-io-logs/federated-region-v3.json
delete: s3://nats-io-logs/federated-region-v2.json
delete: s3://nats-io-logs/federated-region-v4.json
delete: s3://nats-io-logs/federated-region.json
delete: s3://nats-io-logs/federated-region-v5.json
{code}
#* Multiple object delete using {{aws s3api delete-objects}}
{code}
[root@e-37 ~]# cat delete.json
{
  "Objects": [
    {
      "Key": "federated-region.json"
    },
    {
      "Key": "federated-region-v2.json"
    },
    {
      "Key": "federated-region-v3.json"
    },
    {
      "Key": "federated-region-v4.json"
    },
    {
      "Key": "federated-region-v5.json"
    }
  ],
  "Quiet": false
}

[root@e-37 ~]# aws --ca-bundle euca-ca-0.crt --endpoint-url https://s3.c-06.autoqa.qa1.eucalyptus-systems.com/ s3api delete-objects --bucket nats-io-logs --delete file://delete.json --profile eng-admin
{
    "Deleted": [
        {
            "Key": "federated-region.json"
        },
        {
            "Key": "federated-region-v2.json"
        },
        {
            "Key": "federated-region-v3.json"
        },
        {
            "Key": "federated-region-v4.json"
        },
        {
            "Key": "federated-region-v5.json"
        }
    ]
}
{code}
