*Steps*

# Created an account, and one test user:
{code}
[root@g-12-12 ~]# euare-userlistbypath --region testrunner-admin@
arn:aws:iam::000651989085:user/testrunner-user1
arn:aws:iam::000651989085:user/admin
{code}
# Install AWS CLI and create profiles for admin and the test user
# Use linked {{euca12157-policy.json}} policy and upload it to the test user account:
{code}
[root@g-12-12 ~]# euare-userlistpolicies -u testrunner-user1 --region testrunner-admin@ -v
ec2-cfn-deny-ingress-egress
{
  "Version": "2012-10-17",
  "Statement": [
    {
      "Sid": "Stmt1470753333202",
      "Action": "cloudformation:*",
      "Effect": "Allow",
      "Resource": "*"
    },
    {
      "Sid": "Stmt1470753345871",
      "Action": "ec2:*",
      "Effect": "Allow",
      "Resource": "*"
    },
    {
      "Sid": "Stmt1470753394780",
      "Action": [
        "ec2:RevokeSecurityGroupEgress",
        "ec2:RevokeSecurityGroupIngress"
      ],
      "Effect": "Deny",
      "Resource": "*"
    }
  ]
}

IsTruncated: false
{code}
# Use linked {{cfn-euca12157-initial-stack.json}} template to create stack with test user profile:
{code}
[root@g-12-12 ~]# aws --endpoint-url http://cloudformation.d-30.autoqa.qa1.eucalyptus-systems.com:8773/ cloudformation create-stack --stack-name ContinueRollbackTest --template-body file://cfn-euca12157-initial-stack.json --profile testrunner-user1
{
    "StackId": "arn:aws:cloudformation::000651989085:stack/ContinueRollbackTest/efcaa26c-4da5-4c23-850e-aac9716a061a"
}
[root@g-12-12 ~]# aws --endpoint-url http://cloudformation.d-30.autoqa.qa1.eucalyptus-systems.com:8773/ cloudformation describe-stacks --profile testrunner-user1
