There is a option to prune the history;
{noformat}

iscsiadm -m node -T target -p ip -o delete 

{noformat}

my branch dev/jpdonnelly/EUCA-3782

{noformat}

diff --git a/tools/disconnect_iscsitarget_main.pl b/tools/disconnect_iscsitarget_main.pl
index 8bca06b..9d47901 100755
--- a/tools/disconnect_iscsitarget_main.pl
+++ b/tools/disconnect_iscsitarget_main.pl
@@ -139,6 +139,9 @@ while (@paths > 0) {
   } else {
     run_cmd(1, 1, "$ISCSIADM -m node -p $ip -T $store -I $iface -u");
   }
+ # Delete /var/lib/iscsi/node 
+  run_cmd(1, 1, "$ISCSIADM -m node -p $ip -T $store -o delete");
+
 }

{noformat}
