  "Version":"2011-04-01",
  "Statement":[
   {
    "Effect":"Deny",
    "Action":"ec2:RunInstances",
    "Resource": [
    "arn:aws:ec2:::vmtype/m1.large",
    "arn:aws:ec2:::vmtype/m1.xlarge"
    ]
  }
  ]
}
{code}
