  "Statement":[
   {
    "Effect":"Allow",
    "Action":"ec2:*",
    "Resource": "*"
  },
  {
   "Effect":"Limit",
   "Action":"ec2:RunInstances",
   "Resource": [
    "arn:aws:ec2:::vmtype/m1.medium"
   ],
   "Condition":{
     "NumericLessThanEquals":{
       "ec2:quota-vminstancenumber":"2"
     }
    }
   },
  ]
}
{code}
